package com.bunthach.spring_data_jpa.dto;import com.bunthach.spring_data_jpa.model.Product;import lombok.*;import java.util.List;@Setter@Getter@Data@AllArgsConstructor@NoArgsConstructor@ToStringpublic class ProductRequest {    private String name;    private String email;    private String gender;    private List<Product> products;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getGender() {        return gender;    }    public void setGender(String gender) {        this.gender = gender;    }    public List<Product> getProducts() {        return products;    }    public void setProducts(List<Product> products) {        this.products = products;    }}