package com.bunthach.spring_data_jpa.model;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;@Getter@Setter@Entitypublic class Address {    @Id    @GeneratedValue    private Integer id;    private String streetName;    private String houseName;    private String zipCode;    @OneToOne    @JoinColumn(name = "employee_id")    private Employee employee;}