package com.bunthach.spring_data_jpa.model;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.time.LocalDate;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "tbl_Employees")public class Employee {    @Id    @GeneratedValue    private Integer id;    @Column(nullable = false, unique = true)    private String identifier;    @Column(nullable = false)    private String firstName;    @Column(nullable = false)    private String lastName;    @Column(nullable = false, unique = true)    private String email;    @Column(nullable = false)    private LocalDate birthDate;    @Enumerated(EnumType.STRING)    @Column(nullable = false)    private EmployeeRole role;    @OneToOne    @JoinColumn(name = "address_id")    private Address address;    @ManyToOne    @JoinColumn(name = "department_id")    private Department department;    @ManyToMany    @JoinTable(            name = "employee_mission",            joinColumns = @JoinColumn(name = "employee_id"),            inverseJoinColumns = @JoinColumn(name = "mission_id"))    private List<Mission> missions;    public void setId(Integer id) {        this.id = id;    }    public void setIdentifier(String identifier) {        this.identifier = identifier;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public void setLastName(String lastName) {        this.lastName = lastName;    }    public void setEmail(String email) {        this.email = email;    }    public void setBirthDate(LocalDate birthDate) {        this.birthDate = birthDate;    }    public void setRole(EmployeeRole role) {        this.role = role;    }    public void setAddress(Address address) {        this.address = address;    }    public void setDepartment(Department department) {        this.department = department;    }    public void setMissions(List<Mission> missions) {        this.missions = missions;    }}