package com.bunthach.spring_data_jpa.service.Impl;import com.bunthach.spring_data_jpa.dto.OrderRequest;import com.bunthach.spring_data_jpa.dto.ProductRequest;import com.bunthach.spring_data_jpa.model.Customer;import com.bunthach.spring_data_jpa.repository.CustomerRepository;import com.bunthach.spring_data_jpa.repository.ProductRepository;import com.bunthach.spring_data_jpa.service.CustomerService;import org.springframework.stereotype.Service;@Servicepublic class CustomerServiceImpl implements CustomerService {    private final CustomerRepository customerRepository;    private final ProductRepository productRepository;    public CustomerServiceImpl(CustomerRepository customerRepository, ProductRepository productRepository) {        this.customerRepository = customerRepository;        this.productRepository = productRepository;    }    @Override    public Customer orderPlace(OrderRequest orderRequest) {        return customerRepository.save(orderRequest.getCustomer());    }    @Override    public Customer orderItem(ProductRequest request) {        return null;    }}