package com.bunthach.spring_data_jpa.service.Impl;import com.bunthach.spring_data_jpa.dto.EmployeeRequest;import com.bunthach.spring_data_jpa.model.Employee;import com.bunthach.spring_data_jpa.repository.EmployeeRepository;import com.bunthach.spring_data_jpa.service.EmployeeService;import org.springframework.stereotype.Service;@Servicepublic class EmployeeServiceImpl implements EmployeeService {    private final EmployeeRepository employeeRepository;    public EmployeeServiceImpl(EmployeeRepository employeeRepository) {        this.employeeRepository = employeeRepository;    }    @Override    public Employee addEmployee(EmployeeRequest request) {        return employeeRepository.save(request.getEmployee());    }}